CREATE TABLE CLINIC (ID BIGINT NOT NULL, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE PATIENT (ID BIGINT NOT NULL, NAME VARCHAR(255), CLINIC_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE TREATMENT (ID BIGINT NOT NULL, TTYPE VARCHAR(31), TREATMENTTYPE VARCHAR(255), patient_fk BIGINT, DOSAGE INTEGER, DRUG VARCHAR(255), physician_fk BIGINT, TYPE VARCHAR(255), radiologist_fk BIGINT, surgeon_fk BIGINT, PRIMARY KEY (ID))
CREATE TABLE PROVIDER (ID BIGINT NOT NULL, PTYPE VARCHAR(31), NAME VARCHAR(255), NPI VARCHAR(255), PROVIDERTYPE VARCHAR(255), clinic_fk BIGINT, PRIMARY KEY (ID))
CREATE TABLE RADDATE (ID INTEGER NOT NULL, radiologytreatment_fk BIGINT, PRIMARY KEY (ID))
ALTER TABLE PATIENT ADD CONSTRAINT PATIENT_CLINIC_ID FOREIGN KEY (CLINIC_ID) REFERENCES CLINIC (ID)
ALTER TABLE TREATMENT ADD CONSTRAINT TRTMENTphysicianfk FOREIGN KEY (physician_fk) REFERENCES PROVIDER (ID)
ALTER TABLE TREATMENT ADD CONSTRAINT TRTMENTrdologistfk FOREIGN KEY (radiologist_fk) REFERENCES PROVIDER (ID)
ALTER TABLE TREATMENT ADD CONSTRAINT TREATMENTsurgeonfk FOREIGN KEY (surgeon_fk) REFERENCES PROVIDER (ID)
ALTER TABLE TREATMENT ADD CONSTRAINT TREATMENTpatientfk FOREIGN KEY (patient_fk) REFERENCES PATIENT (ID)
ALTER TABLE PROVIDER ADD CONSTRAINT PROVIDER_clinic_fk FOREIGN KEY (clinic_fk) REFERENCES CLINIC (ID)
ALTER TABLE RADDATE ADD CONSTRAINT RDDTrdlgytrtmentfk FOREIGN KEY (radiologytreatment_fk) REFERENCES TREATMENT (ID)
